import java.util.Scanner;

//น้มุ 10951น๘ A+B-4

public class baekjoon1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		while(s.hasNextInt()) {
			int a = s.nextInt();
			int b = s.nextInt();
			System.out.println(a+b);
		}
	}

}
